    "import requests\n",
    "from bs4 import BeautifulSoup\n",
    "\n",
    "\n",
    "def get_faculty_homepage_urls(url):\n",
    "    print(f\"entered {url}\")\n",
    "    response = requests.get(url)\n",
    "    print(response)\n",
    "    if response.status_code == 200:\n",
    "        soup = BeautifulSoup(response.content, 'lxml')\n",
    "        print(soup)\n",
    "        faculty_urls = []\n",
    "        \n",
    "        faculty_cards = soup.find_all('nav', class_='skip-content')\n",
    "        for card in faculty_cards:\n",
    "            faculty_url = card['aria-label']\n",
    "            print(faculty_url)\n",
    "            faculty_urls.append(faculty_url)\n",
    "        return faculty_urls\n",
    "    else:\n",
    "        print(f\"Failed to access URL: {url}\")\n",
    "        return []\n",
    "\n",
    "def scrape_faculty_info(url):\n",
    "    response = requests.get(url)\n",
    "    if response.status_code == 200:\n",
    "        soup = BeautifulSoup(response.content, 'lxml')\n",
    "        \n",
    "        \n",
